sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},

    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);

        // If the testdate is before rightnow, return true, else return false
        if (testdate.before(rightnow)) {
            return true;
        } else {
            return false;
        }
    },

	isDateToday: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date, and get the local date portion of them.
        var gdToday = new GlideDateTime();
        var gdTest = new GlideDateTime(strDate);
		var today = gdToday.getLocalDate();
		var testdate = gdTest.getLocalDate();

        // If the testdate is on today, return true, else return false
        if (today.compareTo(testdate) == 0) {
            return true;
        } else {
            return false;
        }
    },

    type: 'NeedItUtils'
};
