sysauto_script.script:
var rightnow = new GlideDateTime();

var tomorrow = new GlideDateTime();
tomorrow.addDaysLocalTime(1);

var tasksNearOverdue = new GlideRecord('x_58872_needit_needit_task');
tasksNearOverdue.addQuery('due_date', '>=', rightnow);
tasksNearOverdue.addQuery('due_date', '<=', gs.hoursAgo(-24));
tasksNearOverdue.addQuery('state', '!=', 3);

tasksNearOverdue.query();
// Write a log message for each overdue NeedIt Task Record
while (tasksNearOverdue.next()) {
    gs.info("NeedIt Task record near overdue = " + tasksNearOverdue.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',tasksNearOverdue,tasksNearOverdue.number,gs.getUserName());
}
